import React, { useEffect, useState } from 'react';
import weyeLogo from './eye_w.webp';

const Header = () => {
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);
    const [windowHeight, setWindowHeight] = useState(window.innerHeight);

    useEffect(() => {
        const handleResize = () => {
            setWindowWidth(window.innerWidth);
            setWindowHeight(window.innerHeight);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const signUpUrl = "https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Signup/";
    const signInUrl = "https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Login/";
    const helpUrl = "https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Help/";

    return (
        <header className="header">
            <div className="header-content">
                {/* Frame 17 */}
                <div style={{
                    position: 'absolute',
                    width: '100%',
                    height: windowWidth < 550 ? '8%' : '10%',
                    left: '0px',
                    top: '0px',
                    background: '#005BBB'
                }}></div>

                {/* InSight and Logo */}
                <div style={{
                    position: 'absolute',
                    display: 'flex',
                    alignItems: 'center',
                    fontFamily: 'Crimson Text',
                    fontStyle: 'normal',
                    fontWeight: '400',
                    fontSize: windowWidth < 550 || windowHeight < 550 ? '25px' : '40px',
                    lineHeight: '100%',
                    letterSpacing: '-0.01em',
                    color: '#E4E4E4',
                    left: '39px',
                    top: '2%',
                }}>
                    InSight
                    <img src={weyeLogo} alt="Eye" style={{
                        marginLeft: windowWidth < 550 ? '5px' : '10px',
                        height: windowWidth < 550 || windowHeight < 550 ? '30px' : (windowWidth < 900 ? '35px' : '40px'),
                        width: windowWidth < 550 || windowHeight < 550 ? '30px' : (windowWidth < 900 ? '35px' : '40px'),
                    }} />
                </div>

                {/* Sign Up */}
                <a href={signUpUrl} style={{
                    ...linkStyles,
                    width: calculateLinkWidth(windowWidth),
                    right: calculateLinkRightPosition(windowWidth),
                    top: calculateLinkTopPosition(windowWidth),
                    fontSize: windowWidth < 550 ? '16px' : '20px',
                }}>Sign Up</a>

                {/* Sign In */}
                <a href={signInUrl} style={{
                    ...linkStyles,
                    right: calculateSignInRightPosition(windowWidth),
                    top: calculateLinkTopPosition(windowWidth),
                    fontSize: windowWidth < 550 ? '16px' : '20px',
                }}>Sign In</a>

                {/* Help */}
                <a href={helpUrl} style={{
                    ...linkStyles,
                    right: calculateHelpRightPosition(windowWidth),
                    top: calculateLinkTopPosition(windowWidth),
                    fontSize: windowWidth < 550 ? '16px' : '20px',
                }}>Help</a>
            </div>
        </header>
    );
}

const linkStyles = {
    position: 'absolute',
    fontFamily: 'Crimson Text',
    height: '6%',
    color: '#FFFFFF',
    fontWeight: '400',
    textDecoration: 'none',
};

const calculateLinkWidth = (windowWidth) => {
    if (windowWidth < 600) return '16%';
    if (windowWidth < 700) return '14%';
    if (windowWidth < 800) return '12%';
    if (windowWidth < 900) return '10%';
    return '8%';
};

const calculateLinkRightPosition = (windowWidth) => {
    if (windowWidth < 800) return '7%';
    if (windowWidth < 900) return '5.5%';
    return '4%';
};

const calculateSignInRightPosition = (windowWidth) => {
    if (windowWidth < 600) return '20%';
    if (windowWidth < 700) return '15.5%';
    if (windowWidth < 800) return '12%';
    if (windowWidth < 900) return '8%';
    return '4%';
};

const calculateHelpRightPosition = (windowWidth) => {
    if (windowWidth < 600) return '32%';
    if (windowWidth < 700) return '28%';
    if (windowWidth < 800) return '23.5%';
    if (windowWidth < 900) return '18.5%';
    return '13.5%';
};

const calculateLinkTopPosition = (windowWidth) => {
    return windowWidth < 550 ? '3%' : '3.5%';
};

export default Header;
