import './Review.css';
import Professor from './Unknown.jpeg';
import React, { useEffect, useState } from 'react';

function Main() {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
      const handleResize = () => {
          setWindowWidth(window.innerWidth);
      };
      window.addEventListener('resize', handleResize);
      return () => {
          window.removeEventListener('resize', handleResize);
      };
  }, []);

  return (
    <header>
        <div className="header-container">
            {/* Sign Up */}
            <a href="https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Signup/" style={{
                position: 'absolute',
                width: windowWidth<600 ? '16%':(windowWidth < 700 ? '14%' : 
                (windowWidth < 800 ? '12%' : (windowWidth < 900 ? '10%' : '8%'))),
                height: windowWidth < 550 ? '4.5%' : '6%',//button height
                right: windowWidth < 800 ? '7%' : (windowWidth < 900 ? '5.5%' : '4%'),
                top: windowWidth < 800 ? '2.4%' : (windowWidth < 900 ? '2.2%' : '2%'),
                fontFamily: 'Crimson Text',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                background: '#FFFFFF',
                fontSize: (windowWidth < 550 || windowHeight < 550) ? '16px' : '20px',
                fontWeight: '700',
                borderRadius: '9.26822px',
                color: '#000000',
                textDecoration: 'none'
            }}>
                <button style={{
                    background: 'transparent',
                    border: 'none',
                    padding: '0',
                    margin: '0',
                    fontFamily: 'inherit',
                    fontSize: 'inherit',
                    fontWeight: 'inherit',
                    color: 'inherit',
                    cursor: 'pointer'
                }}>Sign Up</button>
            </a>

            {/* Log In */}
            <a href="https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Login/" style={{
                position: 'absolute',
                fontFamily: 'Crimson Text',
                width: '20%',
                height: '6%',
                right: windowWidth<600 ? '20%':(windowWidth < 700 ? '15.5%' : 
                (windowWidth < 800 ? '12%' : (windowWidth < 900 ? '8%' : '4%'))),
                top: windowWidth < 550 ? '3%' : '3.5%',
                color: '#FFFFFF',
                fontSize: windowWidth < 550 ? '16px' : '20px',
                fontWeight: '400',
                textDecoration: 'none',
            }}>Sign In</a>

             {/* Help */}
             <a href="https://www-student.cse.buffalo.edu/CSE442-542/2024-Spring/cse-442ac/Help/" style={{
                position: 'absolute',
                fontFamily: 'Crimson Text',
                width: '20%',
                height: '6%',
                right: windowWidth<600 ? '32%':(windowWidth < 700 ? '28%' : 
                (windowWidth < 800 ? '23.5%' : (windowWidth < 900 ? '18.5%' : '13.5%'))),
                top: windowWidth < 550 ? '3%' : '3.5%',
                color: '#FFFFFF',
                fontSize: windowWidth < 550 ? '16px' : '20px',
                fontWeight: '400',
                textDecoration: 'none',
            }}>Help</a>
        </div>
    </header>
  );
}

export default Main;
