import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import NavBar from '../navBar/NavBar';
import SearchIcon from '../../images/search_icon.png';
import './homepage.css';

const Homepage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [filter, setFilter] = useState('professors'); // 默认过滤器
  const navigate = useNavigate();

  const handleSearch = () => {
    if (searchTerm.trim() !== '') {
      fetchSearchResults();
    }
  };

  const fetchSearchResults = () => {
    fetch(`searchFilter.php?query=${encodeURIComponent(searchTerm)}&filter=${encodeURIComponent(filter)}`)
      .then(response => response.json())
      .then(data => {
        setSearchResults(data);
      })
      .catch(error => console.error('获取搜索结果时出错：', error));
  };

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const handleResultClick = (professorId) => {
    // 导航到教授页面，使用教授ID
    navigate(`/professor/${professorId}`);
  };

  return (
    <div>
      <NavBar />
      <div className="content-container">
        <div className="text-container">
          <p className="intro-text">我想了解...</p>
        </div>

        <div className="search-container">
          <input
            className="search-input"
            type="text"
            placeholder="输入教授名称"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="search-button" onClick={handleSearch}>
            <img src={SearchIcon} alt="搜索" />
          </button>
          <select value={filter} onChange={handleFilterChange}>
            <option value="professors">教授</option>
            <option value="classes">课程</option>
          </select>
        </div>

        <div className="search-results-container">
          <ul>
            {searchResults.map(item => (
              <li key={item.id} onClick={() => handleResultClick(item.id)}>
                {filter === 'classes' ? `${item.class_title}，授课教授：${item.professor_name}` : item.professors}
              </li>
            ))}
          </ul>
          {searchResults.length === 0 && <p>没有找到结果。</p>}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
